import React, { Component } from 'react'
import '../../App.css'
export default class Item extends Component {
    state = {mouse : false}
    
    handleMouse=(flag)=>{
        return ()=>{
            this.setState({mouse : flag})
        }
    }
    handleCheck=(id)=>{
        
    }
    // 指定事件回调返回值是函数
    render() {
        const {id,name,done}=this.props
        return (
            <li onMouseLeave={this.handleMouse(false)}
                onMouseEnter={this.handleMouse(true)}
                style={{backgroundColor: this.state.mouse ? '#ddd' : 'white'}}
                >
            <label>
                {/* defaultChecked 默认选择，后续可更改 */}
                <input type="checkbox" defaultChecked={done} onChange={this.handleCheck(this.state.id)}/>
                <span>{id},{name},{done}</span>
            </label>
            <button className="btn btn-danger" style={{display:this.state.mouse ? 'block' :'none'}}>删除</button>
            </li>
        )
    }
}
